INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Faut-il instaurer un revenu de base ?', 'proposition d action', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('De meilleures conditions de vie', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une réduction de la pauvreté', 'Permet de réduire drastiquement le nombre de personnes vivant sous le seuil de pauvreté', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une plus grande liberté', 'Permet aux individus de s\'adonner à des activités librement choisies', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Plus de temps libre', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une réduction de la peur du chômage', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Pas de stigmatisation', 'Des économies seraient réalisées en supprimant tout le travail administratif lié à la surveillance des bénéficiaires de l’aide sociale, discutable du fait de son caractère humiliant, intrusif et moralisateur', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une meilleure santé', 'Des entretiens menés au terme de la deuxième année suggèrent une amélioration significative de leur état de santé et un optimisme accru quant à leurs perspectives d’emploi', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Tout dépend du montant', 'Le revenu de base n\'est garant de meilleures conditions de vie que s\'il est d\'un montant suffisant', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Désocialisation, ennui et dépression', 'Le travail est un moteur important d\'inclusion sociale et de satisfaction personnelle', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une harmonisation par le bas pour les chômeurs et les retraités', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une dégradation des conditions de travail', 'Ceux-ci seraient obligés d’accepter du travail à n’importe quel prix pour arrondir leur allocation. Il en résulterait une dégradation du marché du travail et la prolifération de « boulots » mal payés. ', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Plus de justice sociale', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une réduction des inégalités', 'En cela, il établit une égalité entre tous et diminue la distance sociale entre les citoyens. Il apporte ainsi à chacun le témoignage concret de son appartenance à la communauté ; l’égalité parfaite entre le droit des femmes et celui des hommes devient réalité.', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Injustice d\'un revenu de base identique pour riches et pauvres', 'Prendre aux pauvres pour redistribuer à tous – y compris aux riches –, de façon égalitaire, ce n’est pas vraiment ce que l’on peut qualifier de politique socialement progressiste.', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Injustice d\'un revenu de base qui ne tient pas compte du travail fourni', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Des modèles de financement éthiquement douteux', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un meilleur rapport au travail', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('La négociation de meilleures conditions de travail', 'Cette offre d’emplois supplémentaire renforcera le pouvoir compensatoire des salariés et leur permettra de négocier des meilleures conditions de travail et d’existence', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une sécurisation des parcours professionnels', 'un tel dispositif permettrait une forme de sécurisation des parcours professionnels sur le plan financier et inciterait ainsi à davantage de prise de risque dans les choix individuels', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('La valorisation des activités bénévoles', 'Ce travail invisible est considérable à l\'échelle nationale et pourtant il n\'est pas rémunéré. Le revenu de base offrirait alors une compensation monétaire à ce travail effectué sans salaire', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('La possibilité de se libérer du travail salarié', 'nous libérer de l\'aliénation au travail et à la consommation, à réduire la place du travail dans nos vies', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('La suppression des trappes à inactivité', ' le revenu de base ne crée pas de trappe à inactivité, c\'est-à-dire de situation dans laquelle il est plus intéressant économiquement d\'être au chômage que d\'avoir un emploi', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une marginalisation des plus pauvres', '« Maintenant on vous a donné un peu d’argent, on ne veut plus vous entendre »', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une bonne chose pour l\'économie du pays', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une incitation à la création d\'entreprises', 'Le revenu de base favorise ainsi le développement d\'activités risquées, ou qui ne sont pas forcément rentables à court terme', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une relance de la consommation', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une réduction du chômage', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une simplification administrative et fiscale', 'moins de dépenses seront affectées à des fins de contrôle, mais davantage pour permettre d’accéder à l’activité (accompagnement des chercheurs d’emploi...)', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Plus personne n\'ira travailler', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une baisse de compétitivité', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Un risque d\'inflation', 'De façon générale, le revenu de base peut à la fois entraîner une baisse de la production et une hausse de la demande', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un risque de fraude pour les expatriés', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une mesure adaptée à l\'économie du 21e siècle', '', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Adaptée à la situation de chômage de masse', 'Il est temps de remettre en question le modèle économique basé sur le plein emploi et que l\'homme se réaccapare sa vie en développant des activités non marchandes', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Adaptée à la situation de fin du travail', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Adaptée à la robotisation du travail', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Adaptée à l\'exigence de flexibilité de la main-d\'œuvre', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Adaptée à la situation de « travail invisible »', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Adaptée au capitalisme cognitif', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un financement irréaliste', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('On a dit de même pour les systèmes des retraites et d\'assurance maladie', 'Il y a quelques siècles, presque personne n\'aurait cru que le suffrage universel ou les systèmes de sécurité sociale comme ceux que nous avons aujourd\'hui seraient possibles. Pourtant, nous y sommes. Il n\'y a aucun obstacle insurmontable à l\'introduction d\'un revenu de base global.', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Des modèles réalistes de financement', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Financement mensuel et non annuel couplé avec la taxe de financement', 'D\'après une association américaine, un prélèvement de 40 % couplé avec un revenu universel de 1000 $ sera financé (30 % pour 750€). Le fait de coupler le prélèvement avec le RU lui-même permet de limiter l\'échange d\'argent et de le bloquer dans le système.', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un désastre économique', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une déconnexion entre revenu et travail', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Un non-sens économique', 'Détacher le revenu du travail n\'a pas de sens car le travail est le seul producteur de richesse, le seul producteur de valeur économique ; donc il y a forcément un lien entre le travail et le salaire', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un principe immoral', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un risque d\'immigration massive', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('L\'immigration est source de richesse', 'Elle participe au développement économique du pays d\'accueil comme des pays d\'origine. Sans immigration, le développement des sciences et des arts n\'aurait pas non plus été ce qu\'il est aujourd\'hui', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Tout dépend des conditions d\'accès au revenu de base', 'être citoyen de la communauté politique, nationale ou supranationale, où se trouverait ouvert un tel droit au revenu. Les résidents permanents étrangers ne bénéficieraient pas de l’Allocation universelle', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Une accentuation de la marchandisation', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un mauvais combat politique', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une mesure à l\'encontre de diverses revendications collectives', 'la proposition du revenu de base va à l\'encontre des revendications exprimées par les collectifs de chômeurs et de femmes, qui aspirent non pas à une allocation plus importante qui leur permettrait de rester chez eux, mais à obtenir un emploi digne.', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une revendication trop imprécise', 'Selon si son montant est faible ou élevé, le revenu de base peut être un progrès ou bien une régression sociale (en particulier pour les versions libérales du revenu de base)', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('L\'abandon de la lutte pour le plein-emploi', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('L\'abandon de la lutte pour une meilleure protection sociale', 'Un grand nombre de défenseurs du revenu de base s\'appuient sur les lacunes du système de protection sociale actuel (assurance chômage, pension de retraite et sécurité sociale) pour lui opposer le dispositif du revenu universel.', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une roue de secours du capitalisme', 'Un revenu de base d\'un montant suffisant pour vivre pleinement amène à faire disparaître le marché du travail, qui est essentiel au bon fonctionnement de l\'économie capitaliste. Au sein de cette économie, seul un revenu de base d\'un montant relativement faible, légitimant la précarité maximale du travail, apparaît donc possible', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Plutôt qu\'un revenu de base, d\'autres revendications', 'L’axe principal devrait donc être la réduction du temps de travail pour toutes et tous, assortie d’une extension du champ de la gratuité pour la satisfaction des besoins sociaux. De ce point de vue, la RTT et l’interdiction des licenciements posent concrètement la question d’une « démarchandisation » de la force de travail', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Un dispositif appliquable dès maintenant', 'Opinion', '0');
