INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Faut-il privatiser ADP ?', 'Proposition d\'action', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('L’Etat n’est pas le plus qualifié pour gérer des aéroports', '', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Une entreprise privée spécialisée serait plus compétente', 'Aujourd’hui, un aéroport comme Roissy, qui compte parmi les premiers au monde en termes de trafic, est largement distancé en termes de qualité de service : 37e selon Skytrax, 128e selon AirHelp ! Face à la concurrence internationale, il est nécessaire d’investir massivement', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Les performances actuelles d\'ADP ne sont pas bonnes et pas près de s’améliorer', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('La gestion des infrastructures aéroportuaires parisiennes n’est qu’une petite part des activités du groupe ADP', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('L’Etat n’a plus les moyens d’investir pour développer le groupe', 'L’Etat n’a plus les moyens d’accompagner cette ambition, de construire par exemple un nouveau terminal à 9 millions d\'euros.', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('L\'État a besoin d’argent', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Privatiser ADP devrait faire rentrer 8 à 10 millions d\'euros les caisses de l\'État', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('C’est un bon moment pour vendre', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Il n\'est pas prévu que le produit de la vente entre dans le budget de l\'Etat', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('L’Etat pourrait céder des participations moins sensibles', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('La privatisation d\'ADP va créer de l\'emploi', 'Ouvrir le capital d’ADP permettra un développement profitable à l’emploi (non-délocalisable), aux usagers et aux salariés', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('C\'est à l\'Etat de gérer les aéroports', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce que ce sont des infrastructures stratégiques', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce que les orientations stratégiques doivent être contrôlées démocratiquement', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Parce que la crise écologique oblige à sortir d’une logique concurrentielle', 'Dire non à la privatisation, c’est aussi dire que nous ne voulons plus du monde qu’ils incarnent, qu’ils promeuvent. « Concurrence », « croissance », « compétition », « déficit », « mondialisation » … Ces mots nous sont répétés comme des mantras, pour guider la société et nos existences, et pour vendre à la découpe nos biens nationaux.', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce qu’ADP est en situation de monopole sur la région parisienne', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('L\'Etat n\'a pas besoin d\'être propriétaire pour assurer un contrôle', 'Privatiser ADP, cela ne veut pas dire privatiser les frontières ni même l’infrastructure. Les fonctions de souveraineté seront intégralement préservées (contrôle des personnes, frontières, douanes ou trafic aérien).', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Un État stratège est préférable à un État rentier', 'En se dégageant de la gestion d’une entreprise commerciale, pour laquelle il n’est pas nécessairement le meilleur, l’Etat pourra se concentrer sur ses missions régaliennes de sécurité et de régulation', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('La privatisation d\'ADP serait une mauvaise affaire pour l\'Etat', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Parce qu\'ADP est rentable et rapporte des dividendes à l\'Etat', 'Quel est le sens de vendre un bijou de famille pour rembourser moins de 0,2 % de la dette française et financer des start-ups, alors qu’entre 2006 et 2018, l’Etat actionnaire a engrangé 1,32 milliards d’euros de dividendes, 173 millions d’euros rien qu’en 2018 ', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Parce que la privatisation des autoroutes n\'a pas été une bonne affaire pour l\'Etat', 'Le précédent de la vente autoroutes à Vinci et consorts parle de lui-même. Les autoroutes ont été vendues environ 10 milliards d’euros en dessous de leur prix de marché, et rapportent chaque année 4 à 5 milliards d’euros de dividendes aux actionnaires !', 'Fait', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce que la concession risque de se prolonger à perpétuité', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('La privatisation d\'ADP serait une mauvaise affaire pour les compagnies aériennes et les voyageurs', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce qu\'il y a un risque de dérapage des tarifs', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `cnature`, `completude`) VALUES ('Parce que cela pourrait pénaliser Air France par rapport à ses concurrents si les tarifs augmentaient suite à la privatisation', 'Opinion', '0');
INSERT INTO `debatsido`.`citation` (`cnom`, `texte`, `cnature`, `completude`) VALUES ('Il faut rejeter les valeurs et le monde incarnés par la privatisation d\'ADP', 'Nous aspirons, au contraire, à la coopération, à l’entraide, à placer les humains et la planète avant les chiffres, il en va de notre survie commune. ', 'Opinion', '0');
